var container=document.querySelector(".screenshot"),tabs=document.querySelectorAll(".tab"),activeTab=tabs[0];for(let e=0;e<tabs.length;e++)tabs[e].addEventListener("click",function(e,t){activeTab.classList.remove("is-active"),activeTab=this,e.target.classList.add("is-active","is-pressed"),setTimeout(()=>{e.target.classList.remove("is-pressed")},120);const s=activeTab.textContent.toLowerCase();container.className=`screenshot ${s}`});